import React from 'react';
import { connect } from 'react-redux';
import './App.css';
import TableData from '../components/table';
import {requestEvents, requestStats} from '../actions';
import { Grid, Menu, Segment } from 'semantic-ui-react'


const mapStateToProps = state => {
  return {
    events: state.events,
    stats: state.stats
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onStatsRequest: () => dispatch(requestStats()),
    onEventsRequest: () => dispatch(requestEvents())
  }
}


class App extends React.Component {

  state = { activeItem: 'bio' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  componentDidMount(){
    this.props.onStatsRequest();
    this.props.onEventsRequest();
  }

  render() {
    const { activeItem } = this.state
    if(this.props.stats.length & this.props.events.length){
      return(
        <div className="App">
          <h1>Hello World</h1>

          <Grid>
            <Grid.Column width={2}>
              <Menu fluid vertical tabular>
                <Menu.Item
                  name='bio'
                  active={activeItem === 'bio'}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  name='pics'
                  active={activeItem === 'pics'}
                  onClick={this.handleItemClick}
                />
              </Menu>
            </Grid.Column>

            <Grid.Column stretched width={12}>
              <Segment style={{overflow: 'auto', maxHeight: 500, }}>
                <TableData data={this.props.stats}/>
              </Segment>
            </Grid.Column>
          </Grid>
        </div>
      );
    }

    return(
      <div>
        <h1>...Loading</h1>
      </div>
    )
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
