import React from 'react';
import { Checkbox, Form } from 'semantic-ui-react'


class SelectMetric extends React.Component {

    constructor(props){
        super(props);
        this.state = {}
    }

    handleChange = (e, { value }) => this.setState({ value })

    render() {
        const {toggle, onSelect} = this.props;
        const ckb = toggle.map( (k, i) => { 
        return <Form.Field>
           <Checkbox 
                key={i}
                radio
                label={k}
                name='checkboxRadioGroup'
                style={{marginBottom:15, display: 'block'}} 
                value="this"
                checked={this.state.value === {k}}
                onChange={this.handleChange}
            />
        </Form.Field>
    });

        return(
            <Form>
                {ckb}
            </Form> 
        )
    }
}

export default SelectMetric
