import React from 'react';
import { connect } from 'react-redux';
import './App.css';
import TableData from '../components/table';
import {requestEvents, requestStats, setSearchField} from '../actions';
import { Grid, Menu, Segment, Container, Input } from 'semantic-ui-react'

const mapStateToProps = state => {
  return {
    searchField: state.searchData.searchField,
    events: state.requestData.events,
    stats: state.requestData.stats
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSearch: (e) => dispatch(setSearchField(e.target.value)),
    onStatsRequest: () => dispatch(requestStats()),
    onEventsRequest: () => dispatch(requestEvents())
  }
}

class App extends React.Component {

  constructor(){
    super()
    this.state = { activeItem: 'Stats' }
  }

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name })
  }

  componentDidMount(){
    this.props.onStatsRequest();
    this.props.onEventsRequest();
  }


  render() {
    const {stats, events, onSearch, searchField} = this.props
    const { activeItem } = this.state

    let tableInfo = stats;
    switch (activeItem){

      case 'Stats':
        tableInfo = stats; 
        break;
      case 'Events':
        tableInfo = events; 
        break;
      default:
        break;

    }

    const filteredTableData = searchField === '' ? tableInfo : [];

    if(stats.length & events.length ){
      console.log(tableInfo.filter( obj => {console.log(obj.poi_id.parseInt() === searchField); return (obj.poi_id === searchField)}))
      return(
        <div className="App">
          <h1>EQ Works Sample Problem</h1>
          <Container>
            <Grid>

              <Grid.Column width={4}>
                <Menu fluid vertical tabular>
                  <Menu.Item name='Stats' active={activeItem === 'Stats'} onClick={this.handleItemClick} />
                  <Menu.Item name='Events' active={activeItem === 'Events'} onClick={this.handleItemClick} />
                  <Menu.Item>
                    <Input icon='search' placeholder='Search...' onChange={onSearch} />
                  </Menu.Item>
                </Menu>
              </Grid.Column>

              <Grid.Column stretched width={12}>
                <Segment style={{overflow: 'auto', maxHeight: 700, }}>
                  <TableData data={filteredTableData}/> {/* Display data with tableData component */}
                </Segment>
              </Grid.Column>

            </Grid>
          </Container>
        </div>
      );

    }

    return(
      <div>
        <h1>...Loading</h1>
      </div>
    )
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
