import React, {useEffect, useRef} from 'react';

// Variables
const GOOGLE_MAP_API_KEY = 'AIzaSyDtxAtICzrgcWE9ntlfGjudzCdNzY7IN5c';
const myLocation = { // CN Tower Landmark
    lat: 43.642567,
    lng: -79.387054
};
// styles
const mapStyles = {
    width: 'auto',
    height: '600px',
};

const HeatMap = ({data, poi}) => {
    // refs
    const googleMapRef = React.createRef();
    const googleMap = useRef(null);
    const heatmap = useRef(null);
    const heatmapData = useRef(null);

    // helper functions
    const createGoogleMap = () =>
        new window.google.maps.Map(googleMapRef.current, {
            zoom: 10,
            center: {
                lat: myLocation.lat,
                lng: myLocation.lng
            }
        }
    );

    // const createMarker = () =>
    //     new window.google.maps.Marker({
    //         position: {lat: myLocation.lat, lng: myLocation.lng},
    //         map: googleMap.current
    // });


    const heatMapData = (lat, lng) => [
        new window.google.maps.LatLng(lat, lng),

    ];

    const heatMap = (radius) => new window.google.maps.visualization.HeatmapLayer({
        data: heatmapData.current,
        map: googleMap.current,
        radius
    });


    // useEffect Hook
    useEffect(() => {
        const googleMapScript = document.createElement('script');
        // googleMapScript.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_MAP_API_KEY}&libraries=visualization`
        window.document.body.appendChild(googleMapScript);

        googleMapScript.addEventListener('load', () => {
            googleMap.current = createGoogleMap();
            // marker.current = createMarker();
            heatmapData.current = heatMapData(43.6708, -79.3899);
            heatmap.current = heatMap(30)
        })
    });

    return (
        <div
            id="google-map"
            ref={googleMapRef}
            style={mapStyles}
        />
    )

}


export default HeatMap;
