import {
    SEARCH_FIELD,
    REQUEST_STATS,
    REQUEST_EVENTS
} from './constants';

const initialSearch = {
    searchField: ''
}

export const setSearchField = (state=initialSearch, action={}) => {

    switch(action.type) {
        case SEARCH_FIELD:
            return { ...state, searchField: action.payload }
        default:
            return state;
    }
}

const initialData = {
    stats: [],
    events: []
}

export const requestData = (state = initialData, action={}) => {
    switch (action.type){

        case REQUEST_STATS:
            return { ...state, stats: action.payload };
        case REQUEST_EVENTS:
            return { ...state, events: action.payload };
        default:
            return state;
    }
}
