import React from 'react';
import { connect } from 'react-redux';
import './App.css';
import TableData from '../components/table';
import {requestEvents, requestStats} from '../actions';
import { Grid, Menu, Segment, Container } from 'semantic-ui-react'


const mapStateToProps = state => {
  return {
    events: state.events,
    stats: state.stats
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onStatsRequest: () => dispatch(requestStats()),
    onEventsRequest: () => dispatch(requestEvents())
  }
}


class App extends React.Component {

  state = { activeItem: 'bio' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  componentDidMount(){
    this.props.onStatsRequest();
    this.props.onEventsRequest();
  }


  render() {
    const { activeItem } = this.state

    let table = this.props.stats;

    switch (activeItem){
      case 'Stats':
        table = this.props.stats; 
        break;
      case 'Events':
        table = this.props.events; 
        break;
      default:
        break;
    }

    if(this.props.stats.length & this.props.events.length){
      return(
        <div className="App">
          <h1>EQ Works Sample Problem</h1>
          <Container>
            <Grid>
              <Grid.Column width={2}>
                <Menu fluid vertical tabular>
                  <Menu.Item
                    name='Stats'
                    active={activeItem === 'Stats'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='Events'
                    active={activeItem === 'Events'}
                    onClick={this.handleItemClick}
                  />
                </Menu>
              </Grid.Column>

              <Grid.Column stretched width={12}>
                <Segment style={{overflow: 'auto', maxHeight: 500, }}>

                  <TableData data={table}/>

                </Segment>
              </Grid.Column>
            </Grid>
          </Container>
        </div>
      );
    }

    return(
      <div>
        <h1>...Loading</h1>
      </div>
    )
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
