import React from 'react';
import { Checkbox } from 'semantic-ui-react'


const SelectMetric = ({toggle, onSelect}) => {

    let disable = {
        index : null,
        set : false
    }

    const handleSelected = (i, data) => {
        disable.index = i;
        disable.set = data;
        onSelect(i,data)
    }

    const ckb = toggle.map( (k, i) => <Checkbox 
            toggle 
            label={k} 
            style={{marginBottom:15, display: 'block'}} 
            key={i} 
            onChange={(e, data) => handleSelected(i, data.checked)}
            disabled={(disable.set & i !== disable.index) ? disable.set : false}
        /> 
    );
    console.log(disable)
    return(
        <div>
            <p>Metrics</p>
            {ckb}
        </div>

    )
}

export default SelectMetric
